@Path("student")
public class StudResource {
	
	StudDao dao =new StudDao();
	
	@GET
	@Path("getStudDetails")
	@Produces(MediaType.APPLICATION_JSON)
	public List<Stud> getStud() {
		return dao.getAllStud();
	}	
	
	@GET
	@Path("getStudDetails/{id}")
	@Produces(MediaType.APPLICATION_JSON)
	public Stud studById(@PathParam("id")int studRollno) {
		return dao.getStudById(studRollno);
		}
	
	@POST
	@Path("addStudDetails")
	@Produces(MediaType.APPLICATION_JSON)
	public List <Stud> addStud(Stud stud) {
		return dao.createStud(stud);
		}
	
	@DELETE
	@Path("deleteStudDetails/{id}")
	@Produces(MediaType.APPLICATION_JSON)
	public List<Stud> removeStud(@PathParam("id")int studRollno) {
		return dao.removeStud(studRollno);
		}
	
	@PUT
	@Path("updateStudDetails/{id}")
	@Produces(MediaType.APPLICATION_JSON)
	public List<Stud> updateStud(@PathParam("id")int studRollno,Stud stud) {
	return dao.updateStud(studRollno,stud.getStudName());
		
		}
	
	

}
                                                                          